mapper.select:
SELECT *
FROM jumelage
WHERE TRUE =TRUE

    AND jumelage.etudiant = #{etudiant}


    AND jumelage.session_id = #{session_id}


    AND jumelage.mentore_par = #{mentore_par}


    AND jumelage.cours_id = #{cours_id}

mapper.delete:
DELETE
FROM jumelage
WHERE TRUE = TRUE

    AND jumelage.etudiant = #{etudiant}


    AND jumelage.session_id = #{session_id}


    AND jumelage.mentore_par = #{mentore_par}


    AND jumelage.cours_id = #{cours_id}
